class TodoApp {
    constructor() {
        this.todos = [];
        this.currentFilter = 'all';
        this.editingId = null;

        this.loadFromStorage();
        this.bindEvents();
        this.render();
    }

    bindEvents() {
        const newTodoInput = document.querySelector('.new-todo');
        const toggleAllCheckbox = document.querySelector('.toggle-all');
        const clearCompletedButton = document.querySelector('.clear-completed');
        const todoList = document.querySelector('.todo-list');

        newTodoInput.addEventListener('keypress', (e) => {
            if (e.key === 'Enter' && e.target.value.trim()) {
                this.addTodo(e.target.value.trim());
                e.target.value = '';
            }
        });

        toggleAllCheckbox.addEventListener('change', (e) => {
            this.toggleAll(e.target.checked);
        });

        clearCompletedButton.addEventListener('click', () => {
            this.clearCompleted();
        });

        todoList.addEventListener('change', (e) => {
            if (e.target.classList.contains('toggle')) {
                const id = e.target.closest('li').dataset.id;
                this.toggleTodo(id);
            }
        });

        todoList.addEventListener('click', (e) => {
            if (e.target.classList.contains('destroy')) {
                const id = e.target.closest('li').dataset.id;
                this.deleteTodo(id);
            }
        });

        todoList.addEventListener('dblclick', (e) => {
            if (e.target.tagName === 'LABEL') {
                const li = e.target.closest('li');
                this.editTodo(li.dataset.id);
            }
        });

        todoList.addEventListener('keydown', (e) => {
            if (e.target.classList.contains('edit')) {
                if (e.key === 'Enter') {
                    this.saveEdit(e.target);
                } else if (e.key === 'Escape') {
                    this.cancelEdit();
                }
            }
        });

        todoList.addEventListener('blur', (e) => {
            if (e.target.classList.contains('edit')) {
                this.saveEdit(e.target);
            }
        }, true);

        window.addEventListener('hashchange', () => {
            this.updateFilter();
        });

        this.updateFilter();
    }

    addTodo(text) {
        const todo = {
            id: Date.now().toString(),
            text: text,
            completed: false
        };
        this.todos.push(todo);
        this.saveToStorage();
        this.render();
    }

    toggleTodo(id) {
        const todo = this.todos.find(t => t.id === id);
        if (todo) {
            todo.completed = !todo.completed;
            this.saveToStorage();
            this.render();
        }
    }

    deleteTodo(id) {
        this.todos = this.todos.filter(t => t.id !== id);
        this.saveToStorage();
        this.render();
    }

    toggleAll(completed) {
        this.todos.forEach(todo => {
            todo.completed = completed;
        });
        this.saveToStorage();
        this.render();
    }

    clearCompleted() {
        this.todos = this.todos.filter(todo => !todo.completed);
        this.saveToStorage();
        this.render();
    }

    editTodo(id) {
        this.editingId = id;
        this.render();
        const input = document.querySelector(`[data-id="${id}"] .edit`);
        if (input) {
            input.focus();
            input.setSelectionRange(input.value.length, input.value.length);
        }
    }

    saveEdit(input) {
        const id = input.closest('li').dataset.id;
        const todo = this.todos.find(t => t.id === id);
        const newText = input.value.trim();

        if (todo && newText) {
            todo.text = newText;
            this.saveToStorage();
        } else if (!newText) {
            this.deleteTodo(id);
        }

        this.editingId = null;
        this.render();
    }

    cancelEdit() {
        this.editingId = null;
        this.render();
    }

    updateFilter() {
        const hash = window.location.hash.replace('#/', '');
        this.currentFilter = hash || 'all';

        document.querySelectorAll('.filters a').forEach(link => {
            if (link.getAttribute('href') === `#/${this.currentFilter}` || 
                (this.currentFilter === 'all' && link.getAttribute('href') === '#/')) {
                link.classList.add('selected');
            } else {
                link.classList.remove('selected');
            }
        });

        this.render();
    }

    getFilteredTodos() {
        switch (this.currentFilter) {
            case 'active':
                return this.todos.filter(todo => !todo.completed);
            case 'completed':
                return this.todos.filter(todo => todo.completed);
            default:
                return this.todos;
        }
    }

    render() {
        const todoList = document.querySelector('.todo-list');
        const todoCount = document.querySelector('.todo-count');
        const toggleAll = document.querySelector('.toggle-all');
        const main = document.querySelector('.main');
        const footer = document.querySelector('.footer');

        const filteredTodos = this.getFilteredTodos();
        const activeTodos = this.todos.filter(todo => !todo.completed);
        const completedTodos = this.todos.filter(todo => todo.completed);

        todoList.innerHTML = filteredTodos.map(todo => `
            <li data-id="${todo.id}" class="${todo.completed ? 'completed' : ''} ${this.editingId === todo.id ? 'editing' : ''}">
                <div class="view">
                    <input class="toggle" type="checkbox" ${todo.completed ? 'checked' : ''}>
                    <label>${todo.text}</label>
                    <button class="destroy"></button>
                </div>
                <input class="edit" value="${todo.text}">
            </li>
        `).join('');

        const itemText = activeTodos.length === 1 ? 'item' : 'items';
        todoCount.innerHTML = `<strong>${activeTodos.length}</strong> ${itemText} left`;

        toggleAll.checked = this.todos.length > 0 && activeTodos.length === 0;

        main.style.display = this.todos.length > 0 ? 'block' : 'none';
        footer.style.display = this.todos.length > 0 ? 'block' : 'none';

        const clearCompletedButton = document.querySelector('.clear-completed');
        clearCompletedButton.style.display = completedTodos.length > 0 ? 'block' : 'none';
    }

    saveToStorage() {
        localStorage.setItem('todos', JSON.stringify(this.todos));
    }

    loadFromStorage() {
        const stored = localStorage.getItem('todos');
        if (stored) {
            this.todos = JSON.parse(stored);
        }
    }
}

new TodoApp();

